mapper.update:
update tbl_custom 
	set email=#{email}, age=#{age}
WHERE custom_id= #{custom_id}

mapper.select:
		select custom_id, name, email, age, reg_date
        from tbl_custom
        where custom_id = #{custom_id}

select custom_id, name, email, age, reg_date
from tbl_custom 
where name = #{name} and age = #{age}
